import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import './App.css';

// About Page Component
function About() {
  return (
    <div>
      <h2>About Herbal Remedy System</h2>
      <p>We provide the best herbal remedies for various ailments. All our medicines are natural and carefully sourced.</p>
    </div>
  );
}

// Contact Page Component
function Contact() {
  return (
    <div className="contact-page">
      <h2>Contact Us</h2>
      <p>Email: info@herbalremedysystem.com</p>
      <p>Phone: +123 456 7890</p>

      <h3>Follow Us</h3>
      <div className="social-media-links">
        <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">
          <img src="path/to/facebook-icon.png" alt="Facebook" />
        </a>
        <a href="https://twitter.com" target="_blank" rel="noopener noreferrer">
          <img src="path/to/twitter-icon.png" alt="Twitter" />
        </a>
        <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
          <img src="path/to/instagram-icon.png" alt="Instagram" />
        </a>
        <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer">
          <img src="path/to/linkedin-icon.png" alt="LinkedIn" />
        </a>
      </div>
    </div>
  );
}

// Footer Component
function Footer() {
  return (
    <footer>
      <p>&copy; 2024 Herbal Remedy System. All rights reserved.</p>
    </footer>
  );
}

// Main App Component
function App() {
  const [username, setUsername] = useState(''); // For Sign Up and Login
  const [password, setPassword] = useState(''); // For Sign Up and Login
  const [message, setMessage] = useState('');
  const [medicines, setMedicines] = useState([]);
  const [newMedicine, setNewMedicine] = useState({ name: '', image_url: '', description: '', prescription: '' });
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [editMedicine, setEditMedicine] = useState(null);
  const [showSignUp, setShowSignUp] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const [showCreateForm, setShowCreateForm] = useState(false);

  // State for search and sort
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOption, setSortOption] = useState('name'); // Default sort option

  useEffect(() => {
    fetchMedicines();
  }, []);

  const fetchMedicines = async () => {
    try {
      const response = await axios.get('http://localhost:5000/medicines');
      setMedicines(response.data);
    } catch (error) {
      console.error('Error fetching medicines:', error);
    }
  };

  const handleSignUp = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/signup', { username, password });
      setMessage('User created successfully!');
      setShowSignUp(false);
    } catch (error) {
      console.error('Sign Up Error:', error.response.data);
      setMessage(error.response.data.error);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/login', { username, password });
      setIsLoggedIn(true);
      setMessage('Logged in successfully!');
      setShowLogin(false);
      setUsername('');
      setPassword('');
    } catch (error) {
      console.error('Login Error:', error.response.data);
      setMessage(error.response.data.error);
    }
  };

  const handleLogout = async () => {
    await axios.post('http://localhost:5000/logout');
    setIsLoggedIn(false);
    setMessage('Logged out successfully!');
  };

  const handleAddMedicine = async (e) => {
    e.preventDefault();
    if (editMedicine) {
      await axios.put(`http://localhost:5000/medicines/${editMedicine.id}`, newMedicine);
      setMessage('Medicine updated successfully!');
      setEditMedicine(null);
    } else {
      await axios.post('http://localhost:5000/medicines', newMedicine);
      setMessage('Medicine added successfully!');
    }
    setNewMedicine({ name: '', image_url: '', description: '', prescription: '' });
    fetchMedicines();
  };

  const handleEditMedicine = (medicine) => {
    setEditMedicine(medicine);
    setNewMedicine(medicine);
    setShowCreateForm(true);
  };

  const handleDeleteMedicine = async (id) => {
    await axios.delete(`http://localhost:5000/medicines/${id}`);
    setMessage('Medicine deleted successfully!');
    fetchMedicines();
  };

  const filteredMedicines = medicines.filter((medicine) =>
    medicine.name.toLowerCase().includes(searchTerm.toLowerCase())
  ).sort((a, b) => {
    if (sortOption === 'name') {
      return a.name.localeCompare(b.name);
    }
    return 0;
  });

  return (
    <Router>
      <div className="App">
        <header>
          <h1>Herbal Remedy System</h1>
          <nav>
            <Link to="/">Home</Link>
            <Link to="/about">About</Link>
            <Link to="/contact">Contact</Link>
            {isLoggedIn ? (
              <>
                <button onClick={handleLogout}>Logout</button>
                <Link to="/medicines">Medicines</Link>
              </>
            ) : (
              <>
                <button onClick={() => setShowSignUp(true)}>Sign Up</button>
                <button onClick={() => setShowLogin(true)}>Login</button>
              </>
            )}
          </nav>
        </header>

        <Routes>
          <Route path="/" element={
            <div>
              <h2>Welcome to the Herbal Remedy System</h2>
              <p>Your health is our priority!</p>
              <input
                type="text"
                placeholder="Search medicines"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <select onChange={(e) => setSortOption(e.target.value)} value={sortOption}>
                <option value="name">Sort by Name</option>
                {/* Additional sorting options can be added here */}
              </select>

              <ul>
                {filteredMedicines.map((medicine) => (
                  <li key={medicine.id}>
                    <h3>{medicine.name}</h3>
                    <img src={medicine.image_url} alt={medicine.name} />
                    <p>{medicine.description}</p>
                    <p>Prescription: {medicine.prescription}</p>
                    <button onClick={() => handleEditMedicine(medicine)}>Edit</button>
                    <button onClick={() => handleDeleteMedicine(medicine.id)}>Delete</button>
                  </li>
                ))}
              </ul>

              {showCreateForm && (
                <form onSubmit={handleAddMedicine}>
                  <h3>{editMedicine ? 'Edit Medicine' : 'Add Medicine'}</h3>
                  <input
                    type="text"
                    placeholder="Medicine Name"
                    value={newMedicine.name}
                    onChange={(e) => setNewMedicine({ ...newMedicine, name: e.target.value })}
                    required
                  />
                  <input
                    type="text"
                    placeholder="Image URL"
                    value={newMedicine.image_url}
                    onChange={(e) => setNewMedicine({ ...newMedicine, image_url: e.target.value })}
                    required
                  />
                  <textarea
                    placeholder="Description"
                    value={newMedicine.description}
                    onChange={(e) => setNewMedicine({ ...newMedicine, description: e.target.value })}
                    required
                  />
                  <input
                    type="text"
                    placeholder="Prescription"
                    value={newMedicine.prescription}
                    onChange={(e) => setNewMedicine({ ...newMedicine, prescription: e.target.value })}
                    required
                  />
                  <button type="submit">{editMedicine ? 'Update Medicine' : 'Add Medicine'}</button>
                  <button onClick={() => { setShowCreateForm(false); setEditMedicine(null); }}>Cancel</button>
                </form>
              )}
            </div>
          } />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>

        <footer>
          <Footer />
        </footer>

        {message && <div className="message">{message}</div>}
        {showSignUp && (
          <div className="modal">
            <form onSubmit={handleSignUp}>
              <h2>Sign Up</h2>
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <button type="submit">Sign Up</button>
              <button onClick={() => setShowSignUp(false)}>Cancel</button>
            </form>
          </div>
        )}

        {showLogin && (
          <div className="modal">
            <form onSubmit={handleLogin}>
              <h2>Login</h2>
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <button type="submit">Login</button>
              <button onClick={() => setShowLogin(false)}>Cancel</button>
            </form>
          </div>
        )}
      </div>
    </Router>
  );
}

export default App;
