import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import './App.css';

// About Page Component
function About() {
  return (
    <div>
      <h2>About Herbal Remedy System</h2>
      <p>We provide the best herbal remedies for various ailments. All our medicines are natural and carefully sourced.</p>
    </div>
  );
}

// Contact Page Component
function Contact() {
  return (
    <div className="contact-page">
      <h2>Contact Us</h2>
      <p>Email: info@herbalremedysystem.com</p>
      <p>Phone: +123 456 7890</p>

      <h3>Follow Us</h3>
      <div className="social-media-links">
        <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">
          <img src="path/to/facebook-icon.png" alt="Facebook" />
        </a>
        <a href="https://twitter.com" target="_blank" rel="noopener noreferrer">
          <img src="path/to/twitter-icon.png" alt="Twitter" />
        </a>
        <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
          <img src="path/to/instagram-icon.png" alt="Instagram" />
        </a>
        <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer">
          <img src="path/to/linkedin-icon.png" alt="LinkedIn" />
        </a>
      </div>
    </div>
  );
}

// Footer Component
function Footer() {
  return (
    <footer>
      <p>&copy; 2024 Herbal Remedy System. All rights reserved.</p>
    </footer>
  );
}

// Main App Component
function App() {
  const [username, setUsername] = useState(''); // For Sign Up and Login
  const [password, setPassword] = useState(''); // For Sign Up and Login
  const [message, setMessage] = useState('');
  const [medicines, setMedicines] = useState([]);
  const [newMedicine, setNewMedicine] = useState({ name: '', image_url: '', description: '', prescription: '' });
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [editMedicine, setEditMedicine] = useState(null);
  const [showSignUp, setShowSignUp] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const [showCreateForm, setShowCreateForm] = useState(false);

  // State for search and sort
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOption, setSortOption] = useState('name'); // Default sort option

  useEffect(() => {
    fetchMedicines();
  }, []);

  const fetchMedicines = async () => {
    try {
      const response = await axios.get('http://localhost:5000/medicines');
      setMedicines(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const signup = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/signup', { username, password });
      setMessage('User created successfully');
      setShowSignUp(false); // Hide the sign-up form after successful sign-up
    } catch (error) {
      setMessage('Error creating user');
    }
  };

  const login = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/login', { username, password });
      setMessage('Logged in successfully');
      setIsLoggedIn(true);
      setShowLogin(false); // Hide the login form after successful login
    } catch (error) {
      setMessage('Invalid credentials');
    }
  };

  const addMedicine = async (e) => {
    e.preventDefault();
    if (editMedicine) {
      await axios.put(`http://localhost:5000/medicines/${editMedicine.id}`, newMedicine);
      setMessage('Medicine updated successfully!');
      setEditMedicine(null);
    } else {
      await axios.post('http://localhost:5000/medicines', newMedicine);
      setMessage('Medicine added successfully!');
    }
    fetchMedicines();
    setNewMedicine({ name: '', image_url: '', description: '', prescription: '' });
    setShowCreateForm(false); // Hide form after submission
  };

  const deleteMedicine = async (id) => {
    await axios.delete(`http://localhost:5000/medicines/${id}`);
    setMessage('Medicine deleted successfully!');
    fetchMedicines();
  };

  const editMedicineForm = (medicine) => {
    setNewMedicine(medicine);
    setEditMedicine(medicine);
    setShowCreateForm(true); // Show the form in edit mode
  };

  // Toggle the create medicine form
  const toggleCreateForm = () => {
    setShowCreateForm(!showCreateForm);
    setEditMedicine(null); // Reset the form for creating a new medicine
  };

  // Toggle the Sign Up form
  const toggleSignUpForm = () => {
    setShowSignUp(!showSignUp);
  };

  // Toggle the Login form
  const toggleLoginForm = () => {
    setShowLogin(!showLogin);
  };

  // Function to handle search term change
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Function to handle sort option change
  const handleSortChange = (option) => {
    setSortOption(option);
  };

  // Function to get filtered and sorted medicines
  const getFilteredMedicines = () => {
    let filteredMedicines = medicines.filter((medicine) =>
      medicine.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Sort medicines based on the selected option
    if (sortOption === 'name') {
      filteredMedicines.sort((a, b) => a.name.localeCompare(b.name));
    } else if (sortOption === 'prescription') {
      filteredMedicines.sort((a, b) => a.prescription.localeCompare(b.prescription));
    }

    return filteredMedicines;
  };

  return (
    <Router>
      <div>
        <nav className="navbar">
          <h1>Herbal Remedy System</h1>
          <div className="navbar-links">
            <Link to="/">Home</Link>
            <Link to="/about">About</Link>
            <Link to="/contact">Contact</Link>
            <button onClick={toggleSignUpForm}>Sign Up</button>
            <button onClick={toggleLoginForm}>Login</button>
          </div>
        </nav>

        <Routes>
          <Route
            path="/"
            element={
              <div className="container">
                {message && <p>{message}</p>}

                {/* Search Input */}
                <div className="search-sort">
                  <input
                    type="text"
                    placeholder="Search medicines..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                  />
                  <select value={sortOption} onChange={(e) => handleSortChange(e.target.value)}>
                    <option value="name">Sort by Name</option>
                    <option value="prescription">Sort by Prescription</option>
                  </select>
                </div>

                {/* Toggle Create Medicine Button */}
                {isLoggedIn && (
                  <button onClick={toggleCreateForm} className="create-btn">
                    <span>+</span> {/* Icon for Create */}
                  </button>
                )}

                {/* Medicine Creation Form */}
                {showCreateForm && (
                  <form onSubmit={addMedicine} className="medicine-form">
                    <h2>{editMedicine ? 'Edit Medicine' : 'Create Medicine'}</h2>
                    <input
                      type="text"
                      placeholder="Name"
                      value={newMedicine.name}
                      onChange={(e) => setNewMedicine({ ...newMedicine, name: e.target.value })}
                      required
                    />
                    <input
                      type="text"
                      placeholder="Image URL"
                      value={newMedicine.image_url}
                      onChange={(e) => setNewMedicine({ ...newMedicine, image_url: e.target.value })}
                      required
                    />
                    <textarea
                      placeholder="Description"
                      value={newMedicine.description}
                      onChange={(e) => setNewMedicine({ ...newMedicine, description: e.target.value })}
                      required
                    />
                    <input
                      type="text"
                      placeholder="Prescription (Yes/No)"
                      value={newMedicine.prescription}
                      onChange={(e) => setNewMedicine({ ...newMedicine, prescription: e.target.value })}
                      required
                    />
                    <button type="submit">{editMedicine ? 'Update Medicine' : 'Add Medicine'}</button>
                  </form>
                )}

                {/* Medicines List */}
                {getFilteredMedicines().length === 0 ? (
                  <p>No medicines found.</p>
                ) : (
                  <div className="medicines-list">
                    {getFilteredMedicines().map((medicine) => (
                      <div key={medicine.id} className="medicine-card">
                        <img src={medicine.image_url} alt={medicine.name} />
                        <h3>{medicine.name}</h3>
                        <p>{medicine.description}</p>
                        <p>Prescription: {medicine.prescription}</p>
                        {isLoggedIn && (
                          <div className="medicine-actions">
                            <button onClick={() => editMedicineForm(medicine)}>Edit</button>
                            <button onClick={() => deleteMedicine(medicine.id)}>Delete</button>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            }
          />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>

        <Footer />
      </div>

      {/* Sign Up Modal */}
      {showSignUp && (
        <div className="modal">
          <form onSubmit={signup}>
            <h2>Sign Up</h2>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button type="submit">Sign Up</button>
            <button type="button" onClick={toggleSignUpForm}>Close</button>
          </form>
        </div>
      )}

      {/* Login Modal */}
      {showLogin && (
        <div className="modal">
          <form onSubmit={login}>
            <h2>Login</h2>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button type="submit">Login</button>
            <button type="button" onClick={toggleLoginForm}>Close</button>
          </form>
        </div>
      )}
    </Router>
  );
}

export default App;
