import React, { useEffect, useContext } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Login from './components/Login';
import Signup from './components/Signup';
import MedicineForm from './components/MedicineForm';
import MedicineList from './components/MedicineList';
import Cart from './components/Cart';
import { AuthContextProvider, AuthContext } from './context/AuthContext';

function App() {
  const { isAuthenticated, checkAuthStatus } = useContext(AuthContext);

  useEffect(() => {
    checkAuthStatus(); // Checks auth status on page load (persists login)
  }, [checkAuthStatus]);

  return (
    <AuthContextProvider>
      <Router>
        <Switch>
          {!isAuthenticated ? (
            <>
              <Route exact path="/login" component={Login} />
              <Route exact path="/signup" component={Signup} />
            </>
          ) : (
            <>
              <Route exact path="/medicines" component={MedicineList} />
              <Route exact path="/add-medicine" component={MedicineForm} />
              <Route exact path="/cart" component={Cart} />
            </>
          )}
        </Switch>
      </Router>
    </AuthContextProvider>
  );
}

export default App;
